mapper.select:
SELECT p.*, s.name AS supplier_name, c.name AS category_name,s.id sid,c.id cid FROM t_product p inner JOIN t_supplier s ON p.supplier_id = s.id inner JOIN t_category c ON p.category_id = c.id
and p.code like concat('%',#{code},'%')
and p.category_id = #{category_id}
and p.supplier_id = #{supplier_id}
